// Obtener los datos del nodo anterior (el IF)
const item = $json; // Usamos $json para más seguridad
// Generar el HTML
const htmlCorreo = `
<!DOCTYPE html>
<html lang="es">
<head>
    <meta charset="UTF-8">
    <meta name="viewport" content="width=device-width, initial-scale=1.0">
    <title>Confirmación de Reserva de Sala</title>
    <style>
        body {
            font-family: 'Segoe UI', Tahoma, Geneva, Verdana, sans-serif;
            line-height: 1.6;
            color: #333;
            background-color: #f4f4f4;
            margin: 0;
            padding: 0;
        }
        .container {
            width: 100%;
            max-width: 600px;
            margin: 20px auto;
            background: #ffffff;
            border-radius: 8px;
            overflow: hidden;
            box-shadow: 0 0 15px rgba(0, 0, 0, 0.05);
        }
        .header {
            background-color: #007bff; /* Azul vibrante */
            color: #ffffff;
            padding: 25px 30px;
            text-align: center;
        }
        .header img {
            max-width: 80px;
            margin-bottom: 10px;
            border-radius: 5px;
        }
        .header h1 {
            margin: 0;
            font-size: 28px;
            font-weight: 600;
        }
        .content {
            padding: 30px;
            color: #555;
        }
        .content h2 {
            color: #007bff;
            font-size: 22px;
            margin-top: 0;
            border-bottom: 2px solid #e9ecef;
            padding-bottom: 10px;
            margin-bottom: 20px;
        }
        .info-table {
            width: 100%;
            border-collapse: collapse;
            margin-bottom: 25px;
        }
        .info-table th, .info-table td {
            padding: 12px 15px;
            text-align: left;
            border-bottom: 1px solid #e9ecef;
        }
        .info-table th {
            background-color: #f8f9fa;
            width: 35%;
            font-weight: 600;
            color: #444;
        }
        .info-table td {
            background-color: #ffffff;
            width: 65%;
        }
        .footer {
            background-color: #e9ecef;
            color: #999; /* Color de texto más suave */
            text-align: center;
            padding: 20px 30px;
            font-size: 13px; /* Texto más pequeño */
            border-top: 1px solid #dee2e6;
        }
        .footer p {
            margin: 0;
        }
    </style>
</head>
<body>
    <div class="container">
        <div class="header">
            <h1>Reserva de Sala Confirmada</h1>
        </div>
        <div class="content">
            <p>Estimado Encargado,</p>
            <p>Se ha confirmado una nueva reserva para una de nuestras salas. Por favor, asegúrese de que todo esté preparado según los detalles a continuación:</p>

            <h2>Detalles de la Reserva</h2>
            <table class="info-table">
                <tr>
                    <th>ID de Reserva:</th>
                    <td><strong>${item.ID_Reserva || 'N/A'}</strong></td>
                </tr>
                <tr>
                    <th>Sala Asignada:</th>
                    <td><strong>${item["Sala asignada"] || 'N/A'}</strong></td>
                </tr>
                <tr>
                    <th>Fecha:</th>
                    <td>${item.Fecha || 'N/A'}</td>
                </tr>
                <tr>
                    <th>Horario:</th>
                    <td>${item.horaInicio || 'N/A'} - ${item.horaTermino || 'N/A'}</td>
                </tr>
                <tr>
                    <th>N° Participantes:</th>
                    <td>${item["N° Participantes"] || 'N/A'}</td>
                </tr>
            </table>

            <h2>Información del Solicitante</h2>
            <table class="info-table">
                <tr>
                    <th>Nombre:</th>
                    <td>${item.Nombre || 'N/A'}</td>
                </tr>
                <tr>
                    <th>Correo:</th>
                    <td>${item.Correo || 'N/A'}</td>
                </tr>
                <tr>
                    <th>Área/Departamento:</th>
                    <td>${item["Area/Departamento"] || 'N/A'}</td>
                </tr>
            </table>
            <p>Agradecemos su gestión para garantizar un excelente servicio.</p>
        </div>
        <div class="footer">
            <p>© ${new Date().getFullYear()} - Gestión Interna de Salas</p>
        </div>
    </div>
</body>
</html>
`;

// Devolver el HTML generado en una nueva propiedad llamada "htmlCorreo"
// También mantenemos todos los datos originales
return {
  json: {
    ...item,
    htmlCorreo: htmlCorreo
  }
};
