// ===============================
// n8n Code node (Webhook-ready)
// - Lee datos desde $json.body / $json.query / $json
// - Sanitiza + valida
// - Valida duración mínima y máxima
// - NO permite agendar en el pasado (TZ -03:00)
// - Genera startIso/endIso (con -03:00)
// - Genera fechaYMD (para guardar fecha sí o sí)
// - Genera ReservaID
// ===============================

// -------- CONFIGURACIÓN --------
const DURACION_MIN_MIN = 30;   // 30 minutos
const DURACION_MAX_MIN = 120;  // 2 horas
const TZ_OFFSET = "-03:00";
// --------------------------------

// --- Helpers de sanitización ---
function sanitizeText(str = "") {
  if (!str) return "";
  str = String(str).replace(/<[^>]*>/g, "");
  return str.replace(/\s+/g, " ").trim();
}

function sanitizeNombre(str = "") {
  if (!str) return "";
  str = String(str).replace(/<[^>]*>/g, "");
  str = str.replace(/[^a-zA-ZáéíóúÁÉÍÓÚñÑüÜ\s]/g, "");
  return str.replace(/\s+/g, " ").trim();
}

// Fecha: DD/MM/YYYY | DD-MM-YYYY | YYYY-MM-DD | Excel
// Importante: YYYY-MM-DD se parsea manual para evitar corrimiento por UTC
function parseFecha(fechaRaw) {
  if (!fechaRaw) return null;

  // Excel serial number
  if (typeof fechaRaw === "number") {
    const epoch = (fechaRaw - 25569) * 86400 * 1000;
    const d = new Date(epoch);
    // Normaliza a "solo fecha" local
    return new Date(d.getFullYear(), d.getMonth(), d.getDate());
  }

  const s = String(fechaRaw).trim();

  // YYYY-MM-DD (solo fecha) -> crear en local (NO UTC)
  let m = s.match(/^(\d{4})-(\d{2})-(\d{2})$/);
  if (m) {
    const [, yyyy, mm, dd] = m;
    const d = new Date(Number(yyyy), Number(mm) - 1, Number(dd));
    return isNaN(d.getTime()) ? null : d;
  }

  // DD/MM/YYYY o DD-MM-YYYY
  m = s.match(/^(\d{1,2})[\/-](\d{1,2})[\/-](\d{4})$/);
  if (m) {
    const [, dd, mm, yyyy] = m;
    const d = new Date(Number(yyyy), Number(mm) - 1, Number(dd));
    return isNaN(d.getTime()) ? null : d;
  }

  // Fallback: si viene con hora (ej: 2025-12-13 10:00)
  const d = new Date(s);
  return isNaN(d.getTime()) ? null : d;
}

// Hora: 08:30 | 10:40 a.m. | 14:20
function parseHora(raw) {
  if (!raw) return "";
  let s = String(raw).toLowerCase().trim();

  const m = s.match(/^(\d{1,2}):(\d{2})(?::\d{2})?\s*(a\.m\.|p\.m\.)?$/);
  if (!m) return "";

  let h = Number(m[1]);
  const min = m[2];
  const suf = m[3];

  if (suf) {
    if (suf.includes("p") && h < 12) h += 12;
    if (suf.includes("a") && h === 12) h = 0;
  }

  return `${String(h).padStart(2, "0")}:${min}`;
}

// ===============================
// Inicio lógica principal
// ===============================

// Webhook body / query / root
const root = $json;
const r =
  (root.body && typeof root.body === "object") ? root.body :
  (root.query && typeof root.query === "object") ? root.query :
  root;

const errores = [];

// -------- Lectura de campos --------
const rawNombre = r.nombre || r["Nombre"] || "";
const rawCorreo = r.email || r["Correo"] || r["Correo electrónico"] || "";
const rawArea   = r.area || r["Área"] || r["Area"] || "";
const rawFecha  = r.fecha || r["Fecha"] || "";
const rawInicio = r.horaInicio || r["Hora inicio"] || "";
const rawFin    = r.horaTermino || r["Hora fin"] || "";
const rawPart   = r.participantes || r["Participantes"] || "";
const rawDesc   = r.descripcion || r["Descripción"] || "";

// -------- Sanitización --------
const nombre = sanitizeNombre(rawNombre);
const correo = sanitizeText(rawCorreo);
const area   = sanitizeText(rawArea);
const descripcion = sanitizeText(rawDesc);

// -------- Validaciones --------

// Email
const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
if (!correo || !emailRegex.test(correo)) errores.push("EMAIL INVÁLIDO");

// Fecha (robusta, sin UTC shift)
const fechaObj = parseFecha(rawFecha);
if (!fechaObj) errores.push("FECHA INVÁLIDA");

// Horas
const inicioStr = parseHora(rawInicio);
const finStr    = parseHora(rawFin);

if (!inicioStr) errores.push("HORA INICIO INVÁLIDA");
if (!finStr)    errores.push("HORA TÉRMINO INVÁLIDA");

// ISO fechas + fechaYMD
let startIso = null;
let endIso = null;
let fechaYMD = null;

if (fechaObj && inicioStr && finStr) {
  const yyyy = fechaObj.getFullYear();
  const mm = String(fechaObj.getMonth() + 1).padStart(2, "0");
  const dd = String(fechaObj.getDate()).padStart(2, "0");

  // Fecha para guardar explícitamente
  fechaYMD = `${yyyy}-${mm}-${dd}`;

  startIso = `${fechaYMD}T${inicioStr}:00${TZ_OFFSET}`;
  endIso   = `${fechaYMD}T${finStr}:00${TZ_OFFSET}`;

  const startMs = Date.parse(startIso);
  const endMs   = Date.parse(endIso);

  // ---- NO permitir agendar en el pasado (comparación con TZ_OFFSET) ----
  const nowIso = new Date().toISOString().slice(0, 19) + TZ_OFFSET; // "YYYY-MM-DDTHH:mm:ss-03:00"
  const nowMs = Date.parse(nowIso);

  if (startMs < nowMs) {
    errores.push("NO SE PUEDE AGENDAR EN EL PASADO");
  }

  if (endMs <= startMs) {
    errores.push("RANGO HORARIO INVÁLIDO (FIN <= INICIO)");
  } else {
    const durMin = (endMs - startMs) / 60000;

    if (durMin < DURACION_MIN_MIN) {
      errores.push(`DURACIÓN MÍNIMA ${DURACION_MIN_MIN} MIN`);
    }

    if (durMin > DURACION_MAX_MIN) {
      errores.push(`DURACIÓN MÁXIMA ${DURACION_MAX_MIN} MIN`);
    }
  }
}

// Participantes
const participantes = Number(rawPart);
if (isNaN(participantes) || participantes <= 0) {
  errores.push("NÚMERO DE PARTICIPANTES INVÁLIDO");
}

// -------- Reserva --------
const ReservaID = `RES-${Date.now().toString(36).toUpperCase()}-${Math.floor(Math.random() * 1000)}`;

const summary = `Reserva sala (${ReservaID}) - ${nombre}`;
const description =
  `ReservaID: ${ReservaID}\n` +
  `Fecha: ${fechaYMD || "(sin fecha)"}\n` +
  `Área: ${area}\n` +
  (descripcion ? `Motivo: ${descripcion}\n` : "") +
  `Participantes: ${participantes}\n` +
  `Solicitante: ${correo}`;

const isValid = errores.length === 0;

return [{
  json: {
    // Campos clave
    fechaYMD,          // <-- ahora SIEMPRE la puedes guardar
    fechaRaw: rawFecha,

    startIso,
    endIso,
    summary,
    description,
    ReservaID,

    requesterEmail: correo,
    participantes,
    nombre,
    correo,
    area,
    inicioStr,
    finStr,

    extendedProperties: {
      private: { ReservaID, requesterEmail: correo, fechaYMD },
    },

    isValid,
    errores,
  }
}];

